import java.util.Scanner;

public class App {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter first number (or leave blank): ");
        String firstLine = scanner.nextLine();
        Integer a = null;
        if (!firstLine.trim().isEmpty()) {
            try {
                a = Integer.valueOf(firstLine.trim());
            } catch (NumberFormatException e) {
                System.err.println("Invalid first number: " + firstLine);
                return;
            }
        }

        System.out.print("Enter second number (or leave blank): ");
        String secondLine = scanner.nextLine();
        Integer b = null;
        if (!secondLine.trim().isEmpty()) {
            try {
                b = Integer.valueOf(secondLine.trim());
            } catch (NumberFormatException e) {
                System.err.println("Invalid second number: " + secondLine);
                return;
            }
        }

        try {
            int result = add(a, b);
            System.out.println("Result: " + result);
        } catch (IllegalArgumentException e) {
            System.err.println("Error: " + e.getMessage());
        }

        scanner.close();
    }

    // Add: simple method to calculate sum of two integers
    public static int add(Integer a, Integer b) {
        if (a == null || b == null) {
            throw new IllegalArgumentException("Arguments must not be null");
        }
        return a + b;
    }
}

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class AppTest {

    @Test
    void addTwoPositive() {
        assertEquals(5, App.add(2, 3));
    }

    @Test
    void addWithNegative() {
        assertEquals(0, App.add(2, -2));
    }

    @Test
    void addZero() {
        assertEquals(7, App.add(7, 0));
    }
}
